; ---------------------------------------------------------------------------
; Subroutine to load address of where the error occurred
; ---------------------------------------------------------------------------

; ||||||||||||||| S U B R O U T I N E |||||||||||||||||||||||||||||||||||||||

; sub_5B2:
ShowErrAddress:
		move.w	#$7CA,(a6)
		moveq	#7,d2

-		rol.l	#4,d0
		bsr.s	ShowErrDigit
		dbf	d2,-
		rts
; ---------------------------------------------------------------------------
; loc_5C2:
ShowErrDigit:
		move.w	d0,d1
		andi.w	#$F,d1
		cmpi.w	#$A,d1
		bcs.s	ShowErrDigit_NoOverflow
		addq.w	#7,d1
; loc_5D0:
ShowErrDigit_NoOverflow:
		addi.w	#$7C0,d1
		move.w	d1,(a6)
		rts
; ---------------------------------------------------------------------------
; loc_5D8:
Error_WaitForC:
		bsr.w	ReadJoypads
		cmpi.b	#$20,(Ctrl_1_Press).w
		bne.w	Error_WaitForC
		rts
; End of function ShowErrAddress

