; ||||||||||||||| S U B R O U T I N E |||||||||||||||||||||||||||||||||||||||
; Input our music/sound selection to the sound driver.
; loc_12AC:
sndDriverInput:
		lea	(Sound_Queue).l,a0
		lea	(Z80_RAM+$1B80).l,a1

		cmpi.b	#$80,8(a1)	; is the sound driver still processing a request?
		bne.s	loc_12E0	; if yes, branch

		_move.b	0(a0),d0
		beq.s	loc_12E0
		_clr.b	0(a0)
		move.b	d0,d1
		subi.b	#$FE,d1
		bcs.s	loc_12DC
		addi.b	#$7F,d1
		move.b	d1,3(a1)
		bra.s	loc_12E0

loc_12DC:
		move.b	d0,8(a1)

loc_12E0:
		moveq	#4-1,d1	; this is one digit too high, meaning the first byte of the voice table pointer is overwritten

loc_12E2:
		move.b	1(a0,d1.w),d0
		beq.s	loc_12F6
		tst.b	9(a1,d1.w)
		bne.s	loc_12F6
		clr.b	1(a0,d1.w)
		move.b	d0,9(a1,d1.w)

loc_12F6:
		dbf	d1,loc_12E2
		rts
; End of function sndDriverInput

