		move.b	d0,(HW_Port_1_Control).l	; init port 1 (joypad 1)
		move.b	d0,(HW_Port_2_Control).l	; init port 2 (joypad 2)
		move.b	d0,(HW_Expansion_Control).l	; init port 3 (expansion/extra)
