		move.w	d1,d3
             
-		move.w	(a1)+,(a6)	; from source address to destination in VDP
		dbf	d3,-		; next tile
		add.l	d4,d0		; increase destination address by $80 (1 line)
		dbf	d2,--		; next line
		rts
